import "./App.css";
import { loggedIn, defaultServer } from "./Util.js";

function App() {

    const [isLoggedIn, setIsLoggedIn] = useState(null);

    useEffect(() => {
        async function check() {
            const result = await loggedIn(defaultServer);
            setIsLoggedIn(result);
        }
        check();
    }, [defaultServer]);

    // While checking auth state, render nothing or a spinner
    if (isLoggedIn === null) {
        return <div>Checking login...</div>;
    }

    return (
        <div className="App">
            {isLoggedIn ? <HomePage /> : <Login />}
        </div>
    );
}

export default App;
