export var defaultServer = "https://localhost:2000";

export function createBasicAuthHeader(username, password) {
    const credentials = `${username}:${password}`;
    const encoded = btoa(credentials);
    return `Basic ${encoded}`;
}

export function loggedIn(server) {
    var token = localStorage.getItem(server + ":authToken");
    console.log(server + ":authToken");
    console.log("token is:" + token);
    if (!token) {
        return false;
    }
    try {
        const response = fetch(server + "/1/run", {
            method: "GET",
            headers: { "Authorization": "Bearer " + token }
        });

        if (response.status === 200) {
            return true;
        } else {
            return false;
        }
    }
    catch (err) {
        return false
    };
}
