import { useState } from "react";
import styles from "./Login.module.css";
import createBasicAuthHeader from "./Util.js";

export default function Login() {
const [server, setServer] = useState("https://localhost:1443");
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [remember, setRemember] = useState(false);
const [error, setError] = useState(null);
const [loading, setLoading] = useState(false);

const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    console.log("HERE 1")

    try {
        console.log("HERE 2")
        const response = await fetch(server + "/1/run", {
            method: "GET",
            headers: { "Authorization": createBasicAuthHeader(username, password) }
        });
        console.log("HERE 3")

        if (response.status === 200) {
            console.log("HERE 4")
            const data = await response.json();
            console.log(data)
            if (remember) {
                if (data && data.token) {
                    localStorage.setItem("authToken", data.token);
                } else {
                    localStorage.setItem("server", server);
                    localStorage.setItem("username", username);
                    localStorage.setItem("password", password);
                }
            }
            window.location.href = "/dashboard";
        } else {
            setError("Invalid username or password");
        }
        } catch (err) {
            setError("Network error");
        } finally {
            setLoading(false);
        }
    };

return (
    <div className={styles.container}>
        <div className={styles.card}>
            <h2 className={styles.title}>Argo DNS Login</h2>
            <form onSubmit={handleSubmit} className={styles.form}>

            <label className={styles.label}>
            Server URL
            <input
                type="text"
                value={server}
                onChange={(e) => setServer(e.target.value)}
                className={styles.input}
                required
            />
            </label>

            <label className={styles.label}>
            Username
            <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className={styles.input}
                required
            />
            </label>

            <label className={styles.label}>
            Password
            <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className={styles.input}
                required
            />
            </label>

            <label className={styles.remember}>
            <input
                type="checkbox"
                checked={remember}
                onChange={(e) => setRemember(e.target.checked)}
            />
            Remember Me
            </label>

            {error && <p className={styles.error}>{error}</p>}

            <button type="submit" className={styles.button} disabled={loading}>
            {loading ? "Logging in..." : "Login"}
            </button>
        </form>
        </div>
    </div>
);
}
